mapper.update:
update member m
set m.member_notify_count = m.member_notify_count+1, m.member_status = '이용정지'
where m.member_id = ${memberId}

mapper.select:
select f.member_id
from (
    SELECT member_id
    FROM freeze
    WHERE member_id = ${memberId}
    ORDER BY freeze_end_date DESC
) f
WHERE ROWNUM = 1
